def on_update_interval():
    global curseBar
    if curse_level > 0 and gameStarted == 1:
        curseBar = statusbars.create(20, 4, StatusBarKind.magic)
        curseBar.value = curse_level
        curseBar.max = 3
        curseBar.set_color(8, 5)
        curseBar.set_label("curse", 5)
        curseBar.position_direction(CollisionDirection.BOTTOM)
        curseBar.set_offset_padding(0, 2)
game.on_update_interval(1000, on_update_interval)

def on_forever():
    if platCheck == 1 and characterAnimations.matches_rule(mySprite,
        characterAnimations.rule(Predicate.HITTING_WALL_LEFT)):
        characterAnimations.set_character_animations_enabled(mySprite, False)
    elif platCheck == 1 and characterAnimations.matches_rule(mySprite,
        characterAnimations.rule(Predicate.HITTING_WALL_RIGHT)):
        characterAnimations.set_character_animations_enabled(mySprite, False)
    else:
        characterAnimations.set_character_animations_enabled(mySprite, True)
forever(on_forever)

def on_forever2():
    if deathCheck > 0 and controller.A.is_pressed():
        pause(500)
        gameOver()
forever(on_forever2)
