scene.onOverlapTile(SpriteKind.Player, myTiles.tile48, function (sprite, location) {
    noSkip = true
    story.startCutscene(function () {
        if (Alter_activated == false) {
            story.printDialog("You see a strange alter!", 85, 110, 50, 160)
            story.showPlayerChoices("Take the holy vessel?", "Take the gold?")
            if (story.checkLastAnswer("Take the holy vessel?")) {
                if (curse_level > 0) {
                    reduceCurse()
                    Alter_activated = true
                    story.cancelCurrentCutscene()
                } else {
                    increaseLife()
                    Alter_activated = true
                    story.cancelCurrentCutscene()
                }
            } else if (story.checkLastAnswer("Take the gold?")) {
                info.changeScoreBy(5000)
                Alter_activated = true
                story.cancelCurrentCutscene()
            }
        } else if (Alter_activated == true) {
            timer.throttle("action", 2000, function () {
                story.printDialog("The remaining items on the alter have crumbled to dust.", 85, 110, 50, 160, 15, 1, story.TextSpeed.Fast)
                story.clearAllText()
                story.cancelAllCutscenes()
            })
        }
    })
})
